INSERT INTO `movies` (`movieID`, `title`, `genre`, `plot`, `duration`, `language`, `country`, `director`, `releaseDate`, `rating`, `productionCompany`, `userRatings`, `lead`, `supporting`, `extras`, `images`) VALUES
(1, 'Carmencita', 'Documentary, Short', 'Performing on what looks like a small wooden stage, wearing a dress with a hoop skirt and white high-heeled pumps, Carmencita does a dance with kicks and twirls, a smile always on her face.', NULL, '', '', 'William K.L. Dickson', '0000-00-00', 6, 'Edison Manufacturing Company', 2060, 'Carmencita', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BZmUzOWFiNDAtNGRmZi00NTIxLWJiMTUtYzhkZGRlNzg1ZjFmXkEyXkFqcGdeQXVyNDE5MTU2MDE@.jpg'),
(2, 'Le clown et ses chiens', 'Animation, Short', 'Lost short film consisting of 300 painted images. It shows a clown entering a circus ring and greeting the audience before he starts to perform tricks with three dogs. The dogs jump through hoops, walk on a ball and jump over a wand.', NULL, '', '', 'Émile Reynaud', '0000-00-00', 6, '', 277, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BZDI4ZDgwMWQtMjA3ZS00NmU5LTk5MGQtZTMyMGFlMjYyZmFlXkEyXkFqcGdeQXVyNTIzOTk5ODM@.jpg'),
(3, 'Poor Pierrot', 'Animation, Comedy, Short, Romance', 'One night, Arlequin come to see his lover Colombine. But then Pierrot knocks at the door and Colombine and Arlequin hide. Pierrot starts singing but Arlequin scares him and the poor man goes away.', NULL, '', '', 'Émile Reynaud', '0000-00-00', 7, '', 2017, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BYWZiY2U3MjgtMzYzNS00OGUzLWI3OTQtNjkyMmZiZjEzNDc2XkEyXkFqcGdeQXVyOTUzMjk0NDE@.jpg'),
(4, 'Un bon bock', 'Animation, Short', 'Lost 1892 French short animated film directed by Émile Reynaud about a wanderer who orders a good beer (\"Un bon bock\") and meets a traveler who also orders a beer. They enter an argument because a kitchen boy steals their beers.', NULL, '', '', 'Émile Reynaud', '0000-00-00', 5, '', 180, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BOGI5NjI1NjMtMjg4Mi00YTQyLThkYjgtNTI1YjlhNzZkOTZmXkEyXkFqcGdeQXVyNTIzOTk5ODM@.jpg'),
(5, 'Blacksmith Scene', 'Short, Comedy', 'Three men hammer on an anvil and pass a bottle of beer around.', NULL, '', '', 'William K.L. Dickson', '0000-00-00', 6, 'Edison Manufacturing Company', 2784, 'Charles Kayser, John Ott', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BZmRmZThhOTgtYjg2MS00NDNhLTk5MmEtZDk4NjU5NGMxMzNiXkEyXkFqcGdeQXVyMTYzMjc0Nzc3.jpg'),
(6, 'Chinese Opium Den', 'Short', '', NULL, '', '', 'William K.L. Dickson', '0000-00-00', 5, '', 187, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BZjNlZTI1NjYtNjVkZi00MDBlLThjMDQtY2Q2NDZiZjllYmE3XkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
(7, 'Corbett and Courtney Before the Kinetograph', 'Short, Sport', 'James J. Corbett and Peter Courtney meet in a boxing exhibition.', NULL, '', '', 'William K.L. Dickson, William Heise', '0000-00-00', 5, 'Edison Manufacturing Company', 864, 'James J. Corbett, Peter Courtney', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BODA4ZTI3NWYtNzAyYi00Yjc5LTk0NDUtZWIzNzQzYzdkODgyXkEyXkFqcGdeQXVyNTM3MDMyMDQ@.jpg'),
(8, 'Edison Kinetoscopic Record of a Sneeze', 'Documentary, Short', 'A man (Thomas Edison\'s assistant) takes a pinch of snuff and sneezes. This is one of the earliest Thomas Edison films and was the first motion picture to be copyrighted in the United States.', NULL, '', '', 'William K.L. Dickson', '0000-00-00', 5, 'Edison Manufacturing Company', 2202, 'Fred Ott', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNGQ5MjZjYzgtYjJhMC00NDIzLTk0NjgtNmRjZDc1ZGM5MmM5XkEyXkFqcGdeQXVyOTUzMjk0NDE@.jpg'),
(9, 'Miss Jerry', 'Romance', 'The adventures of a female reporter in the 1890s.', NULL, '', '', 'Alexander Black', '0000-00-00', 5, 'Alexander Black Photoplays', 212, 'Blanche Bayliss, William Courtenay, Chauncey Depew', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNTUwNzM4NWYtMDkwOC00ZmQwLTk1OGQtNjAwMmU2MjI3Y2QxXkEyXkFqcGdeQXVyNzQ1NDIxMDQ@.jpg'),
(10, 'Leaving the Factory', 'Documentary, Short', 'Workers leaving the Lumière factory for lunch in Lyon, France in 1895; a place of great photographic innovation and one of the birth places of cinema.', NULL, '', '', 'Louis Lumière', '0000-00-00', 7, 'Lumière', 7585, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BY2EwNzY0YmItOWM2MS00MmQyLWEzYmUtYmQ4ZDNlYjg1OGY0XkEyXkFqcGdeQXVyOTUzMjk0NDE@.jpg'),
(11, 'Akrobatisches Potpourri', 'Documentary, Short', 'The fifth sequence of the \"Wintergartenprogramm (1895)\" by the Skladanowsky Brothers. In it, eight circus performers known as the Grunato Family perform their famous balancing act.', NULL, '', '', 'Max Skladanowsky', '0000-00-00', 5, '', 389, 'Grunato', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNDhhMDVhMjUtYjA3MS00NjVhLWI3MTYtZjM5NjMyNDdjNjI0XkEyXkFqcGdeQXVyNTIzOTk5ODM@.jpg'),
(12, 'The Arrival of a Train', 'Documentary, Short', 'A train arrives at La Ciotat station.', NULL, '', '', 'Auguste Lumière, Louis Lumière', '0000-00-00', 7, 'Lumière', 12910, 'Madeleine Koehler, Marcel Koehler, Mrs. Auguste Lumiere', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BZjE2MGVkMTAtMWIwYy00YzQ5LWE2YTAtMTU2NGJmNGNjY2IyXkEyXkFqcGdeQXVyNjMxMzM3NDI@.jpg'),
(13, 'The Photographical Congress Arrives in Lyon', 'Documentary, Short', 'The photographers who need to participate in the congress of Lyon get off a boat in Neuville-sur-Saône, dividing to the right and left.', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 1971, 'Auguste Lumière, P.J.C. Janssen', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BYmU0ZGY2MWYtY2FiZi00MjM5LTkwMzQtMzBiM2Q3YjQ1YjQ2XkEyXkFqcGdeQXVyODgzNDIwODA@.jpg'),
(14, 'The Waterer Watered', 'Short, Comedy', 'An impudent child plays a prank on a gardener innocently watering his plants.', NULL, '', '', 'Louis Lumière', '0000-00-00', 7, 'Lumière, Société des Etablissements L. Gaumont', 5866, 'François Clerc, Benoît Duval', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNGNjMDkzNDAtOGI1NS00MGIyLWFkZjgtYWNkYzhlZWYwMmIzXkEyXkFqcGdeQXVyNzMzMjU5NDY@.jpg'),
(15, 'Autour d\'une cabine', 'Animation, Short', 'Pre-cinematograph colour animation of a woman and man at the beach.', NULL, '', '', 'Émile Reynaud', '0000-00-00', 6, '', 1182, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BZTE1MWFiMGEtYzEyZS00YmIzLWE3MWUtOTJiMzUzNWFhYzk5XkEyXkFqcGdeQXVyMTYzMjc0Nzc3.jpg'),
(16, 'Boat Leaving the Port', 'Documentary, Short', 'Three men in a rowboat are leaving the harbor.', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 1583, 'Mrs. Auguste Lumiere, Jeanne-Joséphine Lumière', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BOTU5YzI1MWEtMmZmZS00N2U2LThiODktODM1NDQwZTZkNDE1XkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
(17, 'Italienischer Bauerntanz', 'Documentary, Short', 'The first sequence of the \"Wintergartenprogramm (1895)\" by the Skladanowsky Brothers. In it, two children, Ploetz and Larella, perform an Italian peasant dance.', NULL, '', '', 'Emil Skladanowsky, Max Skladanowsky', '0000-00-00', 5, '', 347, 'Ploetz, Larella', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BMmQ5NTVhODgtZTVmZS00ZjhjLWE0YWYtMTk5M2U0ZGFhNjZmXkEyXkFqcGdeQXVyNTIzOTk5ODM@.jpg'),
(18, 'Das boxende Känguruh', 'Short', 'The third sequence of the \"Wintergartenprogramm (1895)\" by the Skladanowsky Brothers. In it, a man and a kangaroo stand up in front of each other with boxing gloves, and simulate a boxing match on a theatre stage.', NULL, '', '', 'Max Skladanowsky', '0000-00-00', 5, '', 628, 'Delaware', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BMGI3NDY2NmUtZmYwNi00OThhLWFhZWUtNmNiMTU3NzJmYmVkXkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
(19, 'The Clown Barber', 'Short, Comedy', 'Clown cuts off customer\'s head and replaces it.', NULL, '', '', 'James Williamson', '0000-00-00', 5, 'Williamson Kinematograph Company', 32, '', NULL, NULL, ''),
(20, 'The Derby 1895', 'Documentary, Short, Sport', 'A stationary camera, looking diagonally across a racetrack toward the infield, records the horses as they race past. Once they are out of view and the race is over, police officers run onto the infield. The crowd moves around.', NULL, '', '', 'Birt Acres', '0000-00-00', 5, 'Birt Acres, Robert W. Paul', 383, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BODBkMmI0NDMtM2Y0Zi00YTU1LWJiODktZTJlYTkxNjA5ZWU0XkEyXkFqcGdeQXVyNTIzOTk5ODM@.jpg'),
(21, 'The Photographical Congress Arrives in Lyon', 'Documentary, Short', 'The photographers who need to participate in the congress of Lyon get off a boat in Neuville-sur-Saône, dividing to the right and left.', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 1971, 'Auguste Lumière, P.J.C. Janssen', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BYmU0ZGY2MWYtY2FiZi00MjM5LTkwMzQtMzBiM2Q3YjQ1YjQ2XkEyXkFqcGdeQXVyODgzNDIwODA@.jpg'),
(22, 'Blacksmith Scene', 'Documentary, Short', 'Two blacksmiths work while the vapors rise in the air. Later, another man arrives and offers a drink.', NULL, '', '', 'Louis Lumière', '0000-00-00', 5, 'Lumière', 1149, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNTU3ZjQ4ZmUtNDlmOC00YWM1LTg0MDUtMTBkOGYwMGYwZjdlXkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
(23, 'The Sea', 'Documentary, Short', 'Several little boys run along a pier, then jump into the ocean.', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 1512, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BODBiN2QxZjktZmMwZi00NWY3LWI0NjktZmE5MzUwMzIyZDNjXkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
(24, 'Opening of the Kiel Canal', 'Short, News', '', NULL, '', '', 'Birt Acres', '0000-00-00', 4, 'Birt Acres, Robert W. Paul', 134, 'Empress Augusta Victoria, Kaiser Wilhelm II', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNWNhYTg2M2EtYjQ2Ni00ZGEzLTkxNTctZDBkNWZjMDA5YTEzXkEyXkFqcGdeQXVyNTIzOTk5ODM@.jpg'),
(25, 'The Oxford and Cambridge University Boat Race', 'Short, News, Sport', 'Although the content of this film is primitive in the extreme - a shot of the traditional Oxford versus Cambridge University Boat Race, filmed on March 30 1895 - this film is of immense historical importance as being the first ever British film—Michael Brooke <michael@everyman.demon.co.uk>', NULL, '', '', 'Birt Acres', '0000-00-00', 4, 'Birt Acres, Robert W. Paul', 45, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BOGFiZjVkYmYtYjkwZC00NWY0LWI0MGItYWYyNzNlMDNlN2Y2XkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
(26, 'The Messers. Lumière at Cards', 'Documentary, Short', 'Two men play cards, as a third watches and a waiter brings drinks. The third man pours drinks as the waiter laughs.', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 1612, 'Antoine Féraud, Antoine Lumière, Félicien Trewey', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNGI0OTA4ODItMTE2My00OTNkLTk2MjYtZTcwZjRkOWVmMWU5XkEyXkFqcGdeQXVyNDE5MTU2MDE@.jpg'),
(27, 'Cordeliers\' Square in Lyon', 'Documentary, Short', 'A stationary camera looks across the boulevard at a diagonal toward one corner of Lyon\'s Cordeliers\' Square. It\'s a long shot, with a great deal of depth of focus. We can see the sky and fronts of four buildings, each four or five stories tall. It\'s a busy thoroughfare, with pedestrians walking in front of the buildings and crossing the boulevard between horse-drawn vehicles. A double-decker bus passes in front of us, pulled by two horses. Various tradesmen pass on wagons. One van passes.—<jhailey@hotmail.com>', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 1214, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BYzc4ZTA4NmItMDdkZS00NWZjLWFjYjQtZjMzYjk5YjVhMTdkXkEyXkFqcGdeQXVyODgzNDIwODA@.jpg'),
(28, 'Fishing for Goldfish', 'Documentary, Short', 'A baby held by his father dips his little hands into a water jug and he can\' t catch the goldfish .', NULL, '', '', 'Louis Lumière', '0000-00-00', 5, 'Lumière', 1142, 'Andrée Lumière, Auguste Lumière', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BYTNkN2I5MGUtMWVmOS00OWE5LWE4ZDMtZjE1NjJiODJkNDc5L2ltYWdlL2ltYWdlXkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
(29, 'Baby\'s Meal', 'Documentary, Short', 'As part of a maiden public film screening at the Salon Indien, on December 28, in Paris, Auguste Lumière pivots the centre of attention around his baby daughter, as he tries to feed her from a spoon.', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 3529, 'Mrs. Auguste Lumiere, Andrée Lumière, Auguste Lumière', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BM2Q0ZDhkM2EtODFlOC00NzRiLWJhMmYtMmI0ZWQ0ZTdmMzBmXkEyXkFqcGdeQXVyNDE5MTU2MDE@.jpg'),
(30, 'Rough Sea at Dover', 'Documentary, Short', 'The sea is quite rough, and at Dover a series of heavy waves pounds against a pier and along the adjacent shoreline. The scene then shifts to a different view of flowing water, and shows a heavy current from a point along a riverbank.', NULL, '', '', 'Birt Acres, Robert W. Paul', '0000-00-00', 5, 'Birt Acres, Robert W. Paul', 898, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BOWFhNTM4MmYtMTBjZi00MWMyLWEyMjYtNjAwODcyM2U2NzM1XkEyXkFqcGdeQXVyNTM3MDMyMDQ@.jpg'),
(31, 'Jumping the Blanket', 'Documentary, Short', 'Outdoors, with a nondescript building in the background, four men stand, each holding the corner of a blanket stretched parallel to the ground. They wear the clothes of laborers. By the back corner on our left stands a uniformed man who seems in charge. A sixth man stands back from the blanket about six paces; he runs forward and takes a leap but stops at the edge of the blanket and is upbraided by the uniformed gent. The same thing happens a second time, but the next three times, he completes a flip, landing on his back in the middle of the blanket, and the four then boost him out toward the camera. The final time seems like it will be his last for awhile.—<jhailey@hotmail.com>', NULL, '', '', 'Louis Lumière', '0000-00-00', 6, 'Lumière', 1052, '', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BNmY3YTM2MTQtNWY5MC00MzQxLWJiMDctNWYzYTI5N2Y1OGEzXkEyXkFqcGdeQXVyODgzNDIwODA@.jpg'),
(32, 'Die Serpentintänzerin', 'Short', 'The seventh sequence of the \"Wintergartenprogramm (1895)\" by the Skladanowsky Brothers. In it, a young female dancer with large, flowing robes, swirls round herself quickly, making her light robe flow around her like a butterfly\'s wings.', NULL, '', '', 'Max Skladanowsky', '0000-00-00', 5, '', 437, 'Ancion', NULL, NULL, 'https://m.media-amazon.com/images/M/MV5BYzEzMGUyNjQtNTRjZC00OGY1LThmMTYtM2UwM2Q0ZWM3NzE2XkEyXkFqcGdeQXVyNzg5OTk2OA@@.jpg'),
